import React, { Component } from 'react'
import LayoutTemplateHeader from './components/LayoutTemplateHeader.jsx'
import LayoutTemplateBanner from './components/LayoutTemplateBanner.jsx'
import LayoutTemplateFooter from './components/LayoutTemplateFooter.jsx'
import LayoutTemplateIntroduce from './components/LayoutTemplateIntroduce.jsx'
import LayoutTemplateServices from './components/LayoutTemplateServices.jsx'
import './App.css';
import {getDevice} from '../js/FormUtil.js'

export default class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      typeView : 1, // 1 is Laptop, 2 is Tablet , 3 is Mobile
    };
    this.handleWindowSizeChange = this.handleWindowSizeChange.bind(this);
  }

  componentWillMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  componentDidMount() {
    this.handleWindowSizeChange();
  }

  handleWindowSizeChange() {
    let typeDevice  =  getDevice(window.innerWidth);
    this.setState({width: window.innerWidth, typeView : typeDevice});
  };

  componentWillReceiveProps(nextProps, nextContext) {
    let type = nextProps.type;
    this.setState({type: type});
  }

  render() {
    let {typeView} = this.state;
    console.log(typeView);
    return (
      <div id="wrapper">
        <LayoutTemplateHeader />
        <LayoutTemplateBanner />
        <main id="main" className="site-main haslayout">
          <div className="fw-page-builder-content">
            <section className="tg-main-section haslayout" style={{ backgroundColor: "#f7f7f7", backgroundRepeat: "no-repeat", backgroundPosition: "0% 100%", backgroundSize: "cover", display: "flex" }}  >
              <div className="container" style={{ paddingRight: "5px", paddingLeft: "5px", marginRight: "0", marginLeft: "0", display:"flex", width:"100%"}}>
                <LayoutTemplateIntroduce/>
              </div>
            </section>
            <section className="tg-main-section haslayout" style={{ backgroundColor: "#f7f7f7", backgroundRepeat: "no-repeat", backgroundPosition: "0% 100%", backgroundSize: "cover", padding: "0 0 100px 0" }}>
              <LayoutTemplateServices />
            </section>
          </div>
        </main>
        <LayoutTemplateFooter/>
      </div>
    );
  }
}
