        if (window.document.getElementById("nextT")) {
            next = window.document.getElementById("nextT");
            next.style.display = "none";
        }
        const back = {};
        if (window.document.getElementById("backT")) {
            back = window.document.getElementById("backT");
            back.style.display = "none";
        }

        let testimonialOld = assign({}, testimonials.customers);
        let styleContainerAux = testimonialOld[testimonials.customers.length - 1].styleContainer;
        let styleImageAux = testimonialOld[testimonials.length - 1].styleImage;
        let styleTextAux = testimonialOld[testimonials.length - 1].styleText;

        let i = testimonials.length - 1;
        while (i > 0) {
