            currentCarousel = 0
        } else {
            currentCarousel = currentCarousel + 1
        }

        this.setState({ testimonials, currentCarousel, displayUpDown: false });

        setTimeout(function () {
            next.style.display = "flex";
            back.style.display = "flex";
        }.bind(this), 400);*/
    }

    render() {
        let { customers } = this.state;
        let { primaryColor } = this.props;
        return (
            <div className="layout-customer-testimonial" style={{ width: "100%" }}>
                {
                    customers.length !== 0 ?
                        customers.map((customer, index) => {
                            return (
                                customer.styleContainer !== '' ?
                                    <div key={index} className={customer.styleContainer}>
                                        <div className={customer.styleText}>
                                            <div className={customer.styleImage}>
                                                <img src={customer.image} alt="" />
                                            </div>
                                            <div>
                                                <div id={"testimonialContent" + index} className="testimonial-content">
                                                    {customer.experience}
                                                </div>
                                            </div>
                                            <div>
                                                <h4>{customer.name}</h4>
                                            </div>
                                            <div className="carousel-content-social-network">
                                                <a><i className="fa fa-facebook icon-facebook" /></a>
                                                <a><i className="fa fa-instagram icon-instagram" /></a>
                                                <a><i className="	fa fa-twitter icon-twitter" /></a>
                                            </div>
                                        </div>
                                    </div> : null)
                        })
                        : null
                }
                <div id="nextT" className="content-up-down">
                    <div id="semiUp" className="semi-circle-up" style={{ border: "1px solid " + primaryColor }} onClick={this.slideUp}>
                        <a>
                            <i id="iconUpArrow" className="fa fa-chevron-up up" style={{ color: primaryColor }} />
                        </a>
                    </div>
                </div>
                <div id="backT" className="content-up-down">
                    <div id="semiDown" className="semi-circle-down" style={{ border: "1px solid " + primaryColor }}>
                        <a >
                            <i id="iconDownArrow" className="fa fa-chevron-up down" style={{ color: primaryColor }} />
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}
