import React from 'react'
//import 'jspdf-autotable';


export const SliderBar = ({currentPage,totalPages,primaryColor,heightCurrentBar,heightTotalBar}) => {

  const widthBar = Math.round(((1/totalPages)*100)*100)/100;
  const marginLeft = currentPage*widthBar;

  return(
    <div className="slider-bar-total" style={{height:heightTotalBar}}>
      <div className="slider-bar-current"
           style={{width:widthBar+"%",marginLeft:marginLeft+"%",backgroundColor:primaryColor,height:heightCurrentBar, bottom:(heightCurrentBar-heightTotalBar)}}/>
    </div>
  )
};
