import React, { Component } from 'react';


export default class LayoutTemplateServices extends Component {

    constructor(props) {
        super(props);
        this.state = {
            urlFacebook : "https://www.facebook.com/cecistyleutah/"
        };
        this.onFacebook = this.onFacebook.bind(this);
    }

    componentWillMount() {
        window.addEventListener('onload', this.onFacebook);
    }

    componentWillUnmount() {
        window.removeEventListener('onload', this.onFacebook);
    }

    onFacebook(){
        /*setTimeout(function () {
            document.location = "fb://profile/1444139859182900";
            }, 25);
        window.open("https://www.facebook.com/cecistyleutah/","_blank");*/
        /*let isIpad = navigator.userAgent.match(/iPad/i) != null,
          isIphone = !isIpad && ((navigator.userAgent.match(/iPhone/i) != null) || (navigator.userAgent.match(/iPod/i) != null)),
          isIOS = isIpad || isIphone,
          isAndroid = !isIOS && navigator.userAgent.match(/android/i) != null,
          isMobile = isIOS || isAndroid;


            // If it's not an universal app, use IS_IPAD or IS_IPHONE
            if (isIOS) {
                window.location = "fb://profile/1444139859182900";
                setTimeout(function() {
                    // If the user is still here, open the App Store
                    if (!document.webkitHidden) {
                        // Replace the Apple ID following '/id'
                        window.location = 'https://www.facebook.com/cecistyleutah/';
                    }
                }, 25);
            } else if (isAndroid) {
                // Instead of using the actual URL scheme, use 'intent://' for better UX
                window.location = 'fb://profile/1444139859182900';
            }*/

        //setTimeout(function () {
        //    window.location = "https://www.facebook.com/cecistyleutah/";
        //}, 25);
        //window.open("fb://profile/1444139859182900","_blank");

        //console.log(window.location.href)

        let isIpad = navigator.userAgent.match(/iPad/i) != null,
         isIphone = !isIpad && ((navigator.userAgent.match(/iPhone/i) != null) || (navigator.userAgent.match(/iPod/i) != null)),
         isIOS = isIpad || isIphone,
          isAndroid = !isIOS && navigator.userAgent.match(/android/i) != null,
          isMobile = isIOS || isAndroid;

        console.log("isIOS: ", isIOS);
if(document.getElementById("1"))
        document.getElementById("l").src = "fb://";
        //document.location = 'fb://profile/1444139859182900';
        //console.log(document.location = 'fb://profile/1444139859182900')
        setTimeout( function()
        {
            // eslint-disable-next-line no-restricted-globals
            if( confirm( 'You do not seem to have Your App installed, do you want to go download it now?'))
            {
                document.location = 'http://itunes.apple.com/us/app/yourAppId';
            } else
            {
                window.open('https://www.facebook.com/cecistyleutah/',"_blank");
            }
        }, 500);
    }


    render() {
        let {urlFacebook} = this.state;

        return (
            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-4  col-sm-12" >
                        <div className="company-info haslayout">
                            <div className="col service">
                                <div className="border-left">
                                    <h3>OUR SERVICES</h3>
                                </div>
                                <figure className="img-service"> <img src="../images/services/services.jpg" alt="Choosing the Right Alterations"/> </figure>
                                <div className="description">
                                    <p>We are specialists in wedding dresses, dresses, men's suits, etc. We are here to be complicit in those special moments in your life.</p>
                                </div>
                                
                            </div>
                        </div>
                    </div>
                    <div className="col-xs-12 col-md-4  col-sm-6" >
                        <div className="col formation">
                            <div className="border-left">
                                <h3>INFORMATION</h3>
                            </div>
                            <div className="description">
                                <p>Here is some information about Ceci'Style</p>
                            </div>
                            <ul>
                                <li>Due to the Covid-19 we ask you to wear a face mask and respect the social distance of 6 feet.</li>
                                <li>We have parking to our customers.</li>
                                <li>To make an appointment, please contact us by phone at +1 (385) 226 2473 or send an email to info@cecistyle.org.</li>
                            </ul>
                        </div>
                    </div>
                    <div className="col-xs-12 col-md-4  col-sm-6" >
                        <div className="company-info">
                            <div className="address col address-info-wrap">
                                <div className="border-left">
                                    <h3>CECI'STYLE ADDRESS</h3>
                                </div>
                                <div className="description">
                                    <p>We are at</p>
                                </div>
                                <ul className="address-info ">
                                    <li>
                                        <a href="https://goo.gl/maps/9kVjUsFKFMBaMLvt6" target="_blank" rel="noopener noreferrer">
                                            <i className="fa fa-map-marker" style={{fontSize:"19px", color: "#F692BC"}}/>
                                            <address>1347 East 4065 South Millcreeck, Salt Lake City Utah 84124 </address>
                                        </a>
                                    </li>
                                    <li>
                                        <i className="fa fa-tablet" style={{fontSize:"19px", color: "#F692BC"}}/>
                                        <address>+1 (385) 226 2473</address>
                                    </li>
                                    <li>
                                        <i className="fa fa-paper-plane" style={{fontSize:"19px", color: "#F692BC"}}/>
                                        <address><a href="mailto:info@cecistyle.org">info@cecistyle.org</a></address>
                                    </li>
                                </ul>
                                <div className="social-area">
                                    <ul className="social-icon">
                                        <li>
                                            <a title="Facebook"  href='fb://profile/1444139859182900' target="_blank" rel="noopener noreferrer" onClick={this.onFacebook}>
                                                <i className="fa fa-facebook"/>
                                            </a>
                                        </li>
                                        <li>
                                            <a title="Twitter" href="https://twitter.com/style_ceci" target="_blank" rel="noopener noreferrer">
                                                <i className="fa fa-twitter"/>
                                            </a>
                                        </li>
                                        <li>
                                            <a title="Linked in" href="https://www.linkedin.com/in/cecistyle/" target="_blank" rel="noopener noreferrer">
                                                <i className="fa fa-linkedin"/>
                                            </a>
                                        </li>
                                        <li>
                                            <a title="Instagram" href="https://www.instagram.com/cecistyleutah/" target="_blank" rel="noopener noreferrer">
                                                <i className="fa fa fa-instagram"/>
                                            </a>
                                        </li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
